","keccak256":"0x1e1117254b0e777ecbb902f3b9bb7d0b10920b7896e5eaa62b5163046685b28b","license":"BSD-3-Clause-Clear"},"contracts/BlindOracle.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import { FHE, euint8, euint64, externalEuint8, externalEuint64 } from "@fhevm/solidity/lib/FHE.sol";
import { SepoliaConfig } from "@fhevm/solidity/config/ZamaConfig.sol";

/// @title BlindOracle - Privacy-Preserving Prediction Market
/// @notice A production-ready prediction market with three phases: Blind Commitment, Price Discovery, and Free Trading
/// @dev Uses Zama fhEVM for homomorphic encryption with async decryption via Gateway
contract BlindOracle is SepoliaConfig {
    // ============ Enums ============

    /// @notice Market phases
    enum Phase {
        BlindCommitment,      // Phase 1: Users submit encrypted predictions
        Aggregating,          // Phase 2a: Aggregating encrypted bets
        AwaitingDecryption,   // Phase 2b: Waiting for decryption callback
        FreeTrading,          // Phase 3: Open trading with AMM
        Settled               // Phase 4: Market settled, winners can claim
    }

    // ============ Structs ============

    /// @notice User commitment data
    struct Commitment {
        euint8 encryptedPrediction;  // 0 = NO, 1 = YES
        euint64 encryptedAmount;     // Bet amount in wei
        bool hasCommitted;
        bool tokensAllocated;
    }

    // ============ State Variables ============

    /// @notice Current market phase
    Phase public currentPhase;

    /// @notice Market owner (can trigger phase transitions and settlement)
    address public owner;

    /// @notice Event description
    string public eventDescription;

    /// @notice Deadline for blind commitment phase
    uint256 public commitmentDeadline;

    /// @notice Deadline for the event (after which settlement can occur)
    uint256 public eventDeadline;

    // Phase 1: User commitments
    /// @notice User commitments mapping
    mapping(address => Commitment) private commitments;

    /// @notice List of all participants
    address[] public participants;

    // Phase 2: Encrypted aggregates
    /// @notice Encrypted total amount bet on YES
    euint64 private encryptedTotalYes;

    /// @notice Encrypted total amount bet on NO
    euint64 private encryptedTotalNo;

    /// @notice Request ID for aggregate decryption
    uint256 private aggregateDecryptionRequestId;

    /// @notice Whether aggregation has been completed
    bool public isAggregated;

    // Phase 3: Decrypted aggregates and trading
    /// @notice Total amount bet on YES (decrypted)
    uint256 public totalYesAmount;

    /// @notice Total amount bet on NO (decrypted)
    uint256 public totalNoAmount;

    /// @notice Initial price after price discovery (scaled by 1e18)
    uint256 public initialPrice;

    /// @notice YES token balance for each user
    mapping(address => uint256) public yesTokenBalance;

    /// @notice NO token balance for each user
    mapping(address => uint256) public noTokenBalance;

    /// @notice AMM reserve for YES tokens
    uint256 public reserveYes;

    /// @notice AMM reserve for NO tokens
    uint256 public reserveNo;

    /// @notice Constant product k for AMM
    uint256 public constant K_MULTIPLIER = 1e18;

    // Phase 4: Settlement
    /// @notice Whether the market has been settled
    bool public isSettled;

    /// @notice Final outcome: true = YES won, false = NO won
    bool public finalOutcome;

    /// @notice Track if user has claimed rewards
    mapping(address => bool) public hasClaimed;

    // Token allocation tracking
    /// @notice Pending token allocation requests
    mapping(address => uint256) private tokenAllocationRequestIds;

    /// @notice Counter for processed allocations
    uint256 public allocatedCount;

    // ============ Events ============

    event MarketCreated(string eventDescription, uint256 commitmentDeadline, uint256 eventDeadline);
    event PredictionCommitted(address indexed user);
    event PhaseTransitioned(Phase newPhase);
    event AggregationStarted();
    event DecryptionRequested(uint256 requestId);
    event PriceDiscovered(uint256 totalYes, uint256 totalNo, uint256 initialPrice);
    event TokenAllocationRequested(address indexed user, uint256 requestId);
    event TokensAllocated(address indexed user, bool isYes, uint256 amount);
    event TokensPurchased(address indexed user, bool isYes, uint256 amount, uint256 cost);
    event TokensSold(address indexed user, bool isYes, uint256 amount, uint256 payout);
    event MarketSettled(bool outcome);
    event RewardsClaimed(address indexed user, uint256 amount);

    // ============ Modifiers ============

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner can call this");
        _;
    }

    modifier onlyInPhase(Phase _phase) {
        require(currentPhase == _phase, "Invalid phase for this operation");
        _;
    }

    // ============ Constructor ============

    /// @notice Create a new prediction market
    /// @param _eventDescription Description of the event to predict
    /// @param _commitmentDuration Duration of the blind commitment phase (in seconds)
    /// @param _eventDuration Duration until the event occurs (in seconds)
    constructor(
        string memory _eventDescription,
        uint256 _commitmentDuration,
        uint256 _eventDuration
    ) {
        owner = msg.sender;
        currentPhase = Phase.BlindCommitment;
        eventDescription = _eventDescription;
        commitmentDeadline = block.timestamp + _commitmentDuration;
        eventDeadline = block.timestamp + _eventDuration;

        emit MarketCreated(_eventDescription, commitmentDeadline, eventDeadline);
    }

    // ============ Phase 1: Blind Commitment ============

    /// @notice Submit an encrypted prediction and bet amount
    /// @param encryptedPrediction Encrypted prediction (0 = NO, 1 = YES)
    /// @param encryptedAmount Encrypted bet amount in wei
    /// @param predictionProof Zero-knowledge proof for the prediction
    /// @param amountProof Zero-knowledge proof for the amount
    function commitPrediction(
        externalEuint8 encryptedPrediction,
        externalEuint64 encryptedAmount,
        bytes calldata predictionProof,
        bytes calldata amountProof
    ) external payable onlyInPhase(Phase.BlindCommitment) {
        require(block.timestamp < commitmentDeadline, "Commitment period has ended");
        require(!commitments[msg.sender].hasCommitted, "Already committed");
        require(msg.value > 0, "Must send ETH with commitment");

        // Convert external encrypted inputs to internal encrypted types
        euint8 prediction = FHE.fromExternal(encryptedPrediction, predictionProof);
        euint64 amount = FHE.fromExternal(encryptedAmount, amountProof);

        // Store encrypted commitment
        commitments[msg.sender] = Commitment({
            encryptedPrediction: prediction,
            encryptedAmount: amount,
            hasCommitted: true,
            tokensAllocated: false
        });

        participants.push(msg.sender);

        // Grant FHE permissions for user to decrypt their own commitment
        FHE.allowThis(prediction);
        FHE.allow(prediction, msg.sender);
        FHE.allowThis(amount);
        FHE.allow(amount, msg.sender);

        emit PredictionCommitted(msg.sender);
    }

    /// @notice Get my encrypted commitment (only callable by the user who committed)
    /// @return prediction Encrypted prediction
    /// @return amount Encrypted amount
    function getMyCommitment() external view returns (euint8 prediction, euint64 amount) {
        require(commitments[msg.sender].hasCommitted, "No commitment found");
        Commitment storage commitment = commitments[msg.sender];
        return (commitment.encryptedPrediction, commitment.encryptedAmount);
    }

    // ============ Phase 2: Price Discovery ============

    /// @notice Step 1: Aggregate all encrypted bets using FHE operations
    /// @dev Only callable by owner after commitment deadline
    function aggregateBets() external onlyOwner onlyInPhase(Phase.BlindCommitment) {
        require(block.timestamp >= commitmentDeadline, "Commitment period not ended");
        require(participants.length > 0, "No participants");

        // Initialize encrypted aggregates to zero
        encryptedTotalYes = FHE.asEuint64(0);
        encryptedTotalNo = FHE.asEuint64(0);

        // Aggregate all encrypted bets using homomorphic operations
        for (uint256 i = 0; i < participants.length; i++) {
            address participant = participants[i];
            Commitment storage commitment = commitments[participant];

            euint8 prediction = commitment.encryptedPrediction;
            euint64 amount = commitment.encryptedAmount;

            // Create flags: isYes = 1 if prediction == 1, else 0
            euint8 isYes = prediction;
            // isNo = 1 - isYes
            euint8 isNo = FHE.sub(FHE.asEuint8(1), prediction);

            // Convert euint8 flags to euint64 for multiplication
            euint64 isYes64 = FHE.asEuint64(isYes);
            euint64 isNo64 = FHE.asEuint64(isNo);

            // Calculate contributions: amount * flag
            euint64 yesContribution = FHE.mul(amount, isYes64);
            euint64 noContribution = FHE.mul(amount, isNo64);

            // Add to running totals
            encryptedTotalYes = FHE.add(encryptedTotalYes, yesContribution);
            encryptedTotalNo = FHE.add(encryptedTotalNo, noContribution);
        }

        // Grant permissions for this contract to use the aggregated values
        FHE.allowThis(encryptedTotalYes);
        FHE.allowThis(encryptedTotalNo);

        isAggregated = true;

        // Transition to Aggregating phase
        currentPhase = Phase.Aggregating;
        emit AggregationStarted();
        emit PhaseTransitioned(Phase.Aggregating);
    }

    /// @notice Step 2: Request decryption of aggregated totals via Gateway
    /// @dev This initiates async decryption through the KMS
    function requestAggregateDecryption() external onlyOwner onlyInPhase(Phase.Aggregating) {
        require(isAggregated, "Bets not aggregated yet");

        // Prepare ciphertexts for decryption
        bytes32[] memory cts = new bytes32[](2);
        cts[0] = FHE.toBytes32(encryptedTotalYes);
        cts[1] = FHE.toBytes32(encryptedTotalNo);

        // Request decryption with callback to callbackAggregateDecryption
        aggregateDecryptionRequestId = FHE.requestDecryption(
            cts,
            this.callbackAggregateDecryption.selector
        );

        // Transition to AwaitingDecryption phase
        currentPhase = Phase.AwaitingDecryption;
        emit DecryptionRequested(aggregateDecryptionRequestId);
        emit PhaseTransitioned(Phase.AwaitingDecryption);
    }

    /// @notice Callback function for aggregate decryption results
    /// @dev Called by the decryption oracle with decrypted values
    /// @param requestId The request ID from requestDecryption
    /// @param cleartexts ABI-encoded decrypted values
    /// @param decryptionProof KMS signatures and proof data
    function callbackAggregateDecryption(
        uint256 requestId,
        bytes memory cleartexts,
        bytes memory decryptionProof
    ) public {
        require(requestId == aggregateDecryptionRequestId, "Invalid request ID");
        require(currentPhase == Phase.AwaitingDecryption, "Invalid phase");

        // Verify signatures from KMS
        FHE.checkSignatures(requestId, cleartexts, decryptionProof);

        // Decode decrypted values (uint64, uint64)
        (uint64 yesAmount, uint64 noAmount) = abi.decode(cleartexts, (uint64, uint64));

        totalYesAmount = uint256(yesAmount);
        totalNoAmount = uint256(noAmount);

        // Calculate initial price: Price = YES / (YES + NO)
        uint256 totalPool = totalYesAmount + totalNoAmount;
        require(totalPool > 0, "No bets placed");

        initialPrice = (totalYesAmount * 1e18) / totalPool;

        // Initialize AMM reserves
        reserveYes = totalYesAmount;
        reserveNo = totalNoAmount;

        // Transition to Free Trading phase
        currentPhase = Phase.FreeTrading;

        emit PriceDiscovered(totalYesAmount, totalNoAmount, initialPrice);
        emit PhaseTransitioned(Phase.FreeTrading);
    }

    // ============ Token Allocation (After Price Discovery) ============

    /// @notice Request token allocation for a user based on their encrypted commitment
    /// @dev Users must call this after price discovery to get their YES/NO tokens
    function requestTokenAllocation() external onlyInPhase(Phase.FreeTrading) {
        require(commitments[msg.sender].hasCommitted, "No commitment found");
        require(!commitments[msg.sender].tokensAllocated, "Tokens already allocated");

        Commitment storage commitment = commitments[msg.sender];

        // Prepare ciphertexts for decryption (prediction and amount)
        bytes32[] memory cts = new bytes32[](2);
        cts[0] = FHE.toBytes32(commitment.encryptedPrediction);
        cts[1] = FHE.toBytes32(commitment.encryptedAmount);

        // Request decryption with callback
        uint256 requestId = FHE.requestDecryption(
            cts,
            this.callbackTokenAllocation.selector
        );

        tokenAllocationRequestIds[msg.sender] = requestId;

        emit TokenAllocationRequested(msg.sender, requestId);
    }

    /// @notice Callback function for token allocation decryption
    /// @dev Called by the decryption oracle to allocate tokens to a user
    function callbackTokenAllocation(
        uint256 requestId,
        bytes memory cleartexts,
        bytes memory decryptionProof
    ) public {
        require(currentPhase == Phase.FreeTrading, "Invalid phase");

        // Verify signatures from KMS
        FHE.checkSignatures(requestId, cleartexts, decryptionProof);

        // Find the user who made this request
        address user = address(0);
        for (uint256 i = 0; i < participants.length; i++) {
            if (tokenAllocationRequestIds[participants[i]] == requestId) {
                user = participants[i];
                break;
            }
        }

        require(user != address(0), "User not found for request");
        require(!commitments[user].tokensAllocated, "Tokens already allocated");

        // Decode decrypted values (uint8 prediction, uint64 amount)
        (uint8 prediction, uint64 amount) = abi.decode(cleartexts, (uint8, uint64));

        // Allocate tokens based on prediction
        if (prediction == 1) {
            // User bet on YES
            yesTokenBalance[user] = uint256(amount);
        } else {
            // User bet on NO
            noTokenBalance[user] = uint256(amount);
        }

        commitments[user].tokensAllocated = true;
        allocatedCount++;

        emit TokensAllocated(user, prediction == 1, uint256(amount));
    }

    // ============ Phase 3: Free Trading (AMM) ============

    /// @notice Buy YES tokens using constant product AMM
    /// @param minTokensOut Minimum tokens to receive (slippage protection)
    function buyYesTokens(uint256 minTokensOut) external payable onlyInPhase(Phase.FreeTrading) {
        require(msg.value > 0, "Must send ETH");

        // Calculate tokens out using constant product formula
        // k = reserveYes * reserveNo
        // (reserveYes - tokensOut) * (reserveNo + msg.value) = k
        // tokensOut = reserveYes - k / (reserveNo + msg.value)

        uint256 k = reserveYes * reserveNo;
        uint256 newReserveNo = reserveNo + msg.value;
        uint256 newReserveYes = k / newReserveNo;
        uint256 tokensOut = reserveYes - newReserveYes;

        require(tokensOut >= minTokensOut, "Slippage too high");
        require(tokensOut > 0, "Insufficient output amount");

        // Update reserves
        reserveYes = newReserveYes;
        reserveNo = newReserveNo;

        // Update user balance
        yesTokenBalance[msg.sender] += tokensOut;

        emit TokensPurchased(msg.sender, true, tokensOut, msg.value);
    }

    /// @notice Buy NO tokens using constant product AMM
    /// @param minTokensOut Minimum tokens to receive (slippage protection)
    function buyNoTokens(uint256 minTokensOut) external payable onlyInPhase(Phase.FreeTrading) {
        require(msg.value > 0, "Must send ETH");

        // Calculate tokens out using constant product formula
        uint256 k = reserveYes * reserveNo;
        uint256 newReserveYes = reserveYes + msg.value;
        uint256 newReserveNo = k / newReserveYes;
        uint256 tokensOut = reserveNo - newReserveNo;

        require(tokensOut >= minTokensOut, "Slippage too high");
        require(tokensOut > 0, "Insufficient output amount");

        // Update reserves
        reserveYes = newReserveYes;
        reserveNo = newReserveNo;

        // Update user balance
        noTokenBalance[msg.sender] += tokensOut;

        emit TokensPurchased(msg.sender, false, tokensOut, msg.value);
    }

    /// @notice Sell YES tokens back to the AMM
    /// @param tokenAmount Amount of YES tokens to sell
    /// @param minEthOut Minimum ETH to receive (slippage protection)
    function sellYesTokens(uint256 tokenAmount, uint256 minEthOut) external onlyInPhase(Phase.FreeTrading) {
        require(tokenAmount > 0, "Must sell positive amount");
        require(yesTokenBalance[msg.sender] >= tokenAmount, "Insufficient YES tokens");

        // Calculate ETH out using constant product formula
        uint256 k = reserveYes * reserveNo;
        uint256 newReserveYes = reserveYes + tokenAmount;
        uint256 newReserveNo = k / newReserveYes;
        uint256 ethOut = reserveNo - newReserveNo;

        require(ethOut >= minEthOut, "Slippage too high");
        require(ethOut > 0, "Insufficient output amount");

        // Update reserves
        reserveYes = newReserveYes;
        reserveNo = newReserveNo;

        // Update user balance
        yesTokenBalance[msg.sender] -= tokenAmount;

        // Transfer ETH to user
        (bool success, ) = msg.sender.call{value: ethOut}("");
        require(success, "ETH transfer failed");

        emit TokensSold(msg.sender, true, tokenAmount, ethOut);
    }

    /// @notice Sell NO tokens back to the AMM
    /// @param tokenAmount Amount of NO tokens to sell
    /// @param minEthOut Minimum ETH to receive (slippage protection)
    function sellNoTokens(uint256 tokenAmount, uint256 minEthOut) external onlyInPhase(Phase.FreeTrading) {
        require(tokenAmount > 0, "Must sell positive amount");
        require(noTokenBalance[msg.sender] >= tokenAmount, "Insufficient NO tokens");

        // Calculate ETH out using constant product formula
        uint256 k = reserveYes * reserveNo;
        uint256 newReserveNo = reserveNo + tokenAmount;
        uint256 newReserveYes = k / newReserveNo;
        uint256 ethOut = reserveYes - newReserveYes;

        require(ethOut >= minEthOut, "Slippage too high");
        require(ethOut > 0, "Insufficient output amount");

        // Update reserves
        reserveYes = newReserveYes;
        reserveNo = newReserveNo;

        // Update user balance
        noTokenBalance[msg.sender] -= tokenAmount;

        // Transfer ETH to user
        (bool success, ) = msg.sender.call{value: ethOut}("");
        require(success, "ETH transfer failed");

        emit TokensSold(msg.sender, false, tokenAmount, ethOut);
    }

    /// @notice Get quote for buying YES tokens
    /// @param ethIn Amount of ETH to spend
    /// @return tokensOut Amount of YES tokens that would be received
    function getYesBuyQuote(uint256 ethIn) external view returns (uint256 tokensOut) {
        if (currentPhase < Phase.FreeTrading) return 0;
        uint256 k = reserveYes * reserveNo;
        uint256 newReserveNo = reserveNo + ethIn;
        uint256 newReserveYes = k / newReserveNo;
        tokensOut = reserveYes - newReserveYes;
    }

    /// @notice Get quote for buying NO tokens
    /// @param ethIn Amount of ETH to spend
    /// @return tokensOut Amount of NO tokens that would be received
    function getNoBuyQuote(uint256 ethIn) external view returns (uint256 tokensOut) {
        if (currentPhase < Phase.FreeTrading) return 0;
        uint256 k = reserveYes * reserveNo;
        uint256 newReserveYes = reserveYes + ethIn;
        uint256 newReserveNo = k / newReserveYes;
        tokensOut = reserveNo - newReserveNo;
    }

    /// @notice Get quote for selling YES tokens
    /// @param tokenAmount Amount of YES tokens to sell
    /// @return ethOut Amount of ETH that would be received
    function getYesSellQuote(uint256 tokenAmount) external view returns (uint256 ethOut) {
        if (currentPhase < Phase.FreeTrading) return 0;
        uint256 k = reserveYes * reserveNo;
        uint256 newReserveYes = reserveYes + tokenAmount;
        uint256 newReserveNo = k / newReserveYes;
        ethOut = reserveNo - newReserveNo;
    }

    /// @notice Get quote for selling NO tokens
    /// @param tokenAmount Amount of NO tokens to sell
    /// @return ethOut Amount of ETH that would be received
    function getNoSellQuote(uint256 tokenAmount) external view returns (uint256 ethOut) {
        if (currentPhase < Phase.FreeTrading) return 0;
        uint256 k = reserveYes * reserveNo;
        uint256 newReserveNo = reserveNo + tokenAmount;
        uint256 newReserveYes = k / newReserveNo;
        ethOut = reserveYes - newReserveYes;
    }

    /// @notice Get current market price (YES probability)
    /// @return price Current price scaled by 1e18
    function getCurrentPrice() external view returns (uint256 price) {
        if (currentPhase < Phase.FreeTrading) return initialPrice;
        uint256 totalPool = reserveYes + reserveNo;
        if (totalPool == 0) return 0;
        price = (reserveYes * 1e18) / totalPool;
    }

    // ============ Phase 4: Settlement ============

    /// @notice Settle the market with the final outcome
    /// @param outcome The final outcome: true = YES won, false = NO won
    function settleMarket(bool outcome) external onlyOwner onlyInPhase(Phase.FreeTrading) {
        require(block.timestamp >= eventDeadline, "Event has not occurred yet");

        finalOutcome = outcome;
        isSettled = true;
        currentPhase = Phase.Settled;

        emit MarketSettled(outcome);
        emit PhaseTransitioned(Phase.Settled);
    }

    /// @notice Claim rewards after settlement
    /// @dev Winners receive their proportional share of the total pool
    function claimRewards() external onlyInPhase(Phase.Settled) {
        require(!hasClaimed[msg.sender], "Already claimed rewards");
        require(commitments[msg.sender].hasCommitted, "Did not participate");

        uint256 winningTokens = finalOutcome ? yesTokenBalance[msg.sender] : noTokenBalance[msg.sender];
        require(winningTokens > 0, "No winning tokens");

        hasClaimed[msg.sender] = true;

        // Calculate payout: (user's winning tokens / total winning tokens) * total pool
        uint256 totalWinningTokens = finalOutcome ? reserveYes : reserveNo;
        uint256 totalPool = address(this).balance;
        uint256 payout = (winningTokens * totalPool) / totalWinningTokens;

        // Transfer payout to user
        (bool success, ) = msg.sender.call{value: payout}("");
        require(success, "ETH transfer failed");

        emit RewardsClaimed(msg.sender, payout);
    }

    // ============ View Functions ============

    /// @notice Get number of participants
    function getParticipantCount() external view returns (uint256) {
        return participants.length;
    }

    /// @notice Get participant address by index
    function getParticipant(uint256 index) external view returns (address) {
        require(index < participants.length, "Index out of bounds");
        return participants[index];
    }

    /// @notice Get contract balance
    function getBalance() external view returns (uint256) {
        return address(this).balance;
    }

    /// @notice Check if user has committed
    function hasUserCommitted(address user) external view returns (bool) {
        return commitments[user].hasCommitted;
    }

    /// @notice Check if user's tokens have been allocated
    function hasTokensAllocated(address user) external view returns (bool) {
        return commitments[user].tokensAllocated;
    }

    /// @notice Get user's token balances
    function getUserBalances(address user) external view returns (uint256 yesBalance, uint256 noBalance) {
        return (yesTokenBalance[user], noTokenBalance[user]);
    }

    /// @notice Get market info
    function getMarketInfo() external view returns (
        Phase phase,
        uint256 _commitmentDeadline,
        uint256 _eventDeadline,
        uint256 _totalYes,
        uint256 _totalNo,
        uint256 _initialPrice,
        uint256 _currentPrice,
        bool _isSettled,
        bool _finalOutcome
    ) {
        uint256 currentPrice = 0;
        if (currentPhase >= Phase.FreeTrading) {
            uint256 totalPool = reserveYes + reserveNo;
            if (totalPool > 0) {
                currentPrice = (reserveYes * 1e18) / totalPool;
            }
        }

        return (
            currentPhase,
            commitmentDeadline,
            eventDeadline,
            totalYesAmount,
            totalNoAmount,
            initialPrice,
            currentPrice,
            isSettled,
            finalOutcome
        );
    }
}

